        const pageInfoElem = this._pageInfoElem;
        const pageInfoId = pageInfoElem.attr('id');
        arrowElem.attr(_OJ_FILMSTRIP_LABELLEDBY, `${pageInfoId} ${arrowId}`); // @HTMLUpdateOK

