    COMPONENT_CREATE: 1,
    CONVERTER_OPTION_CHANGE: 2,
    DISABLED_OPTION_CHANGE: 3,
    READONLY_OPTION_CHANGE: 4,
    REFRESH_METHOD: 5,
    REQUIRED_OPTION_CHANGE: 6,
    RESET_METHOD: 7,
    USER_ACTION: 8,
    VALIDATE_METHOD: 9,
    VALIDATORS_OPTION_CHANGE: 10,
    VALUE_OPTION_CHANGE: 11
